import { useLocation, useNavigate } from "react-router-dom";
import "./HospitalDoctorSelection.css";

const doctors = {
  1: [
    { id: 1, name: "Dr. Alice Brown", specialty: "Cardiologist", image: "https://source.unsplash.com/400x300/?doctor" },
    { id: 2, name: "Dr. John Smith", specialty: "Neurologist", image: "https://source.unsplash.com/400x300/?surgeon" }
  ],
  2: [
    { id: 3, name: "Dr. Sarah Lee", specialty: "Pediatrician", image: "https://source.unsplash.com/400x300/?nurse" },
    { id: 4, name: "Dr. David Kim", specialty: "Dermatologist", image: "https://source.unsplash.com/400x300/?doctor,man" }
  ]
};

function DoctorSelection() {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const hospitalId = params.get("hospital");
  const navigate = useNavigate();

  if (!hospitalId || !doctors[hospitalId]) {
    return <h2 className="error">Invalid Hospital Selection</h2>;
  }

  return (
    <div className="container">
      <h2 className="title">Select a Doctor</h2>
      <div className="grid">
        {doctors[hospitalId].map((doctor) => (
          <div key={doctor.id} className="card" onClick={() => navigate(`/canvas?doctor=${doctor.id}`)}>
            <img src={doctor.image} alt={doctor.name} className="card-img" />
            <h3 className="card-title">{doctor.name}</h3>
            <p className="card-subtitle">{doctor.specialty}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default DoctorSelection;
