import React from "react";

const PrivacyPolicy = () => {
  const containerStyle = {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#fdf4f9",
    padding: "30px",
    borderRadius: "12px",
    boxShadow: "0 4px 15px rgba(0, 0, 0, 0.1)",
    maxWidth: "900px",
    margin: "50px auto",
    fontFamily: "'Arial', sans-serif",
    gap: "20px",
  };

  const imageStyle = {
    width: "250px",
    height: "auto",
    borderRadius: "12px",
    objectFit: "cover",
  };

  const textContainerStyle = {
    flex: 1,
    textAlign: "left",
    color: "#5a5a5a",
  };

  const headingStyle = {
    fontFamily: "'Comic Sans MS', cursive",
    color: "#ff5c8a",
    fontSize: "26px",
    marginBottom: "10px",
  };

  const paragraphStyle = {
    fontSize: "16px",
    lineHeight: "1.6",
  };

  return (
    <div style={containerStyle}>
      {/* Left Side - Image */}
      <img
        src="https://media.istockphoto.com/id/1026632588/photo/funny-child-girl-draws-laughing-shows-hands-dirty-with-paint.jpg?s=612x612&w=0&k=20&c=7JCNEfWqx9VfnkzQs-jFwbVpk_S7X5xT8mnVE8WK3R0="
        alt="Creative Kids"
        style={imageStyle}
      />

      {/* Right Side - Text */}
      <div style={textContainerStyle}>
        <h1 style={headingStyle}>Privacy Policy</h1>
        <p style={paragraphStyle}>
          Welcome to <strong>Canvas of Care</strong>, a creative platform for children aged 5 to 15 battling cancer.
          We deeply respect and protect the privacy of our young heroes. All personal information is collected with 
          parental consent and used solely to celebrate creativity, connect with families, and ensure a safe and 
          inspiring environment.
        </p>
      </div>
    </div>
  );
};

export default PrivacyPolicy;
