import { useEffect, useState } from "react";
import axios from "axios";

const Gallery = () => {
  const [gallery, setGallery] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchImages = async () => {
      try {
        // Fetch images from your backend
        const response = await axios.get("http://localhost:5000/api/gallery");
        console.log("API Response:", response.data); // Log the response to see its structure
        
        // Check for different possible response structures
        if (Array.isArray(response.data)) {
          setGallery(response.data); // Case where the response itself is an array
        } else if (response.data && Array.isArray(response.data.images)) {
          setGallery(response.data.images); // Case where images are inside a nested "images" array
        } else {
          throw new Error("Unexpected response format");
        }
      } catch (error) {
        console.error("Error fetching images:", error);
        setError("Failed to load images");
      } finally {
        setLoading(false); // Stop loading regardless of success or failure
      }
    };

    fetchImages();
  }, []);

  return (
    <div className="gallery-container" style={{ textAlign: "center", padding: "20px" }}>
      <h3 style={{ marginBottom: "10px" }}>Gallery</h3>
      {loading ? (
        <p>Loading images...</p>
      ) : error ? (
        <p>{error}</p>
      ) : gallery.length > 0 ? (
        <div
          className="gallery-grid"
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
            gap: "15px",
            marginTop: "20px",
          }}
        >
          {gallery.map((image, index) => (
            <div key={index} style={{ position: "relative" }}>
              <img
                src={image.imageUrl || image.url} // Check for both possible properties
                alt={`Drawing ${index + 1}`}
                style={{
                  width: "200px",
                  height: "200px",
                  objectFit: "cover",
                  borderRadius: "10px",
                  boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
                }}
              />
            </div>
          ))}
        </div>
      ) : (
        <p>No images found</p>
      )}
    </div>
  );
};

export default Gallery;
