// src/pages/DoctorSelection.js
import { useNavigate, useParams } from "react-router-dom";

const doctors = {
  1: [
    { id: 1, name: "Dr. Smith" },
    { id: 2, name: "Dr. Johnson" },
  ],
  2: [
    { id: 3, name: "Dr. Patel" },
    { id: 4, name: "Dr. Lee" },
  ],
  3: [
    { id: 5, name: "Dr. Brown" },
    { id: 6, name: "Dr. Williams" },
  ],
};

const DoctorSelection = () => {
  const { hospitalId } = useParams();
  const navigate = useNavigate();
  const selectedDoctors = doctors[hospitalId] || [];

  const handleSelectDoctor = () => {
    navigate("/canvas");
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <h1 className="text-2xl font-bold mb-4">Select a Doctor</h1>
      <div className="space-y-4">
        {selectedDoctors.map((doctor) => (
          <button
            key={doctor.id}
            onClick={handleSelectDoctor}
            className="bg-green-500 text-white px-6 py-3 rounded-lg shadow-md hover:bg-green-600"
          >
            {doctor.name}
          </button>
        ))}
      </div>
    </div>
  );
};

export default DoctorSelection;
