import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import LandingPage from "./LandingPage";
import NoteItCanvas from "./NoteItCanvas";
import HospitalSelection from "./HospitalSelection";
import DoctorSelection from "./DoctorSelection";
import AuthForm from "./AuthForm";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  return (
    <Router>
      <Routes>
        {/* Default Landing Page */}
        <Route path="/" element={<LandingPage />} />

        {/* Authentication Page (Login/Signup) */}
        <Route path="/auth" element={<AuthForm setIsAuthenticated={setIsAuthenticated} />} />

        {/* Protected Routes */}
        {isAuthenticated ? (
          <>
            <Route path="/hospitals" element={<HospitalSelection />} />
            <Route path="/doctors/:hospitalId" element={<DoctorSelection />} />
            <Route path="/canvas/:doctorId" element={<NoteItCanvas />} />
          </>
        ) : (
          // Redirect to login if not authenticated
          <>
            <Route path="/hospitals" element={<Navigate to="/auth" />} />
            <Route path="/doctors/:hospitalId" element={<Navigate to="/auth" />} />
            <Route path="/canvas/:doctorId" element={<Navigate to="/auth" />} />
          </>
        )}
      </Routes>
    </Router>
  );
}

export default App;
