import React, { useState } from "react";
import "./LandingPage.css";

const LandingPage = () => {
  const [isLogin, setIsLogin] = useState(true);

  return (
    <>
      {/* Navbar */}
      <nav className="navbar">
        <h2 className="logo">Canvas of Care</h2>
        <ul>
          <li><a href="#home">Home</a></li>
          <li><a href="#about">About</a></li>
          <li><a href="#fundraiser">Fundraiser</a></li>
        </ul>
      </nav>

      {/* Main Content */}
      <div className="landing-container">
        <div className="auth-box">
          <div className="auth-toggle">
            <button className={isLogin ? "active" : ""} onClick={() => setIsLogin(true)}>Login</button>
            <button className={!isLogin ? "active" : ""} onClick={() => setIsLogin(false)}>Sign Up</button>
          </div>
          <form>
            {!isLogin && <input type="text" placeholder="Full Name" required />}
            <input type="email" placeholder="Email" required />
            <input type="password" placeholder="Password" required />
            <button type="submit">{isLogin ? "Login" : "Sign Up"}</button>
          </form>
        </div>
        <div className="welcome-section">
          <h1>Welcome to Canvas of Care!</h1>
          <p>A safe and fun place where young artists aged 5-15 can create, share, and explore amazing artwork!</p>
          <div className="gallery-preview">
            <img src="https://ihbp.org.in/wp-content/uploads/2024/11/SURAJ-BARUI-HI-18X14.jpg" alt="Artwork 1" />
            <img src="https://ihbp.org.in/wp-content/uploads/2024/11/SURAJ-BARUI-HI-18X14.jpg" alt="Artwork 2" />
          </div>
        </div>
      </div>
    </>
  );
};

export default LandingPage;
