// src/pages/HospitalSelection.js
import { useNavigate } from "react-router-dom";

const hospitals = [
  { id: 1, name: "City Hospital" },
  { id: 2, name: "General Hospital" },
  { id: 3, name: "Sunrise Medical Center" },
];

const HospitalSelection = () => {
  const navigate = useNavigate();

  const handleSelectHospital = (hospitalId) => {
    navigate(`/doctors/${hospitalId}`);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <h1 className="text-2xl font-bold mb-4">Select a Hospital</h1>
      <div className="space-y-4">
        {hospitals.map((hospital) => (
          <button
            key={hospital.id}
            onClick={() => handleSelectHospital(hospital.id)}
            className="bg-blue-500 text-white px-6 py-3 rounded-lg shadow-md hover:bg-blue-600"
          >
            {hospital.name}
          </button>
        ))}
      </div>
    </div>
  );
};

export default HospitalSelection;
