import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./LandingPage.css";

const LandingPage = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();

    if (!email.endsWith("@gmail.com")) {
      alert("Only Gmail accounts are allowed.");
      return;
    }

    if (email && password) {
      navigate("/app");
    } else {
      alert("Please enter email and password.");
    }
  };

  return (
    <div className="canvas-of-care">
      {/* Falling Leaves */}
      <div className="leaves-container">
        {[...Array(8)].map((_, i) => (
          <div key={i} className="leaf">
            <img src={i % 2 === 0 ? "leave1.png" : "leave2.png"} alt="leaf" />
          </div>
        ))}
      </div>

      {/* Navigation */}
      <header className="header">
        <nav className="nav">
          <a href="#about">ABOUT</a>
          <a href="#gallery">GALLERY</a>
          <a href="#fundraisers">FUNDRAISERS</a>
        </nav>
      </header>

      {/* Main Content */}
      <main className="main-content">
        <section className="creative-refuge">
          <h1>CREATIVE REFUGE</h1>
          <h2>Unleash Your Artistic Spirit.</h2>
          <p>
            Experience a nurturing environment where creativity flourishes amidst
            the challenges of cancer. Engage in meaningful self-expression and
            showcase your creations.
          </p>
        </section>

        {/* Login Section */}
        <section className="login-box">
          <h2>Sign In</h2>
          <form className="login-form" onSubmit={handleLogin}>
            <input
              type="email"
              placeholder="Gmail Address"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <button type="submit" className="login-btn">LOGIN</button>
          </form>

          <div className="login-links">
            <a href="#forgot-password">Forgot Password?</a>
            <span className="spacer"></span>
            <a href="#signup">Sign Up</a>
          </div>
        </section>
      </main>
    </div>
  );
};

export default LandingPage;
