import React from "react";
import { useNavigate } from "react-router-dom";
import "./LandingPage.css";

const LandingPage = () => {
  const navigate = useNavigate();

  const handleLogin = () => {
    navigate("/app"); // Navigate to the main app
  };

  return (
    <div className="canvas-of-care">
      {/* Header Section */}
      <header className="header">
        <nav className="nav">
          <a href="#about">ABOUT</a>
          <a href="#gallery">GALLERY</a>
          <a href="#resources">RESOURCES</a>
          <a href="#policies">POLICIES</a>
        </nav>
      </header>

      {/* Main Content Section */}
      <main className="main-content">
        {/* Creative Refuge Section */}
        <section className="creative-refuge">
          <h1>CREATIVE REFUGE</h1>
          <h2>Unleash Your Artistic Spirit.</h2>
          <p>
            Experience a nurturing environment where creativity flourishes amidst
            the challenges of cancer. Use our intuitive platform to engage in
            meaningful self-expression, showcase your creations, and connect
            with others on similar journeys.
          </p>
          <button className="join-us">JOIN US</button>
        </section>

        {/* Login Section */}
        <section className="login-section">
          <h3>Express Yourself Safely</h3>
          <button className="enter-art-studio" onClick={handleLogin}>
            ENTER ART STUDIO
          </button>

          <form className="login-form">
            <input type="email" placeholder="Email Address" required />
            <input type="password" placeholder="Password" required />
            <a href="#forgot-password" className="forgot-password">
              Forgot Password?
            </a>
            <button type="submit" className="sign-up">
              Sign up
            </button>
          </form>
        </section>
      </main>
    </div>
  );
};

export default LandingPage;
