import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./LandingPage.css";

const LandingPage = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();
    if (email && password) {
      navigate("/app"); // Redirect only if both fields are filled
    } else {
      alert("Please enter email and password.");
    }
  };

  return (
    <div className="canvas-of-care">
      {/* Header Section */}
      <header className="header">
        <nav className="nav">
          <a href="#about">ABOUT</a>
          <a href="#gallery">GALLERY</a>
          <a href="#resources">RESOURCES</a>
          <a href="#policies">POLICIES</a>
        </nav>
      </header>

      {/* Main Content Section */}
      <main className="main-content">
        {/* Creative Refuge Section */}
        <section className="creative-refuge">
          <h1>CREATIVE REFUGE</h1>
          <h2>Unleash Your Artistic Spirit.</h2>
          <p>
            Experience a nurturing environment where creativity flourishes amidst
            the challenges of cancer. Use our intuitive platform to engage in
            meaningful self-expression, showcase your creations, and connect
            with others on similar journeys.
          </p>
          /*<button className="join-us">JOIN US</button>*/
        </section>

        {/* Login Section */}
        <section className="login-section">
          <h3>Express Yourself Safely</h3>
          <form className="login-form" onSubmit={handleLogin}>
            <input
              type="email"
              placeholder="Email Address"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />

            <div className="auth-links">
              <a href="#forgot-password" className="forgot-password">
                Forgot Password?
              </a>
              <a href="#signup" className="sign-up">
                Sign Up
              </a>
            </div>

            {/* âœ… Fixed: Changed class from "login-button" to "login-btn" to match CSS */}
            <button type="submit" className="login-btn">
              LOGIN
            </button>
          </form>
        </section>
      </main>
    </div>
  );
};

export default LandingPage;
