import { useNavigate } from "react-router-dom";
import "./HospitalDoctorSelection.css";

const hospitals = [
  { id: 1, name: "City Hospital", image: "https://t3.ftcdn.net/jpg/02/11/15/66/240_F_211156620_CeBr5etdTNXLb231sFcQ8M9YD1OY5IW8.jpg" },
  { id: 2, name: "Green Valley Medical", image: "https://t4.ftcdn.net/jpg/00/00/46/25/240_F_462543_A9k3qeaOLHD9xGCewkhznEXTvifxlh.jpg" }
];

function HospitalSelection() {
  const navigate = useNavigate();

  return (
    <div className="hospital-doctor-container">
      <div className="container">
        <h2 className="title">Select a Hospital</h2>
        <div className="grid">
          {hospitals.map((hospital) => (
            <div key={hospital.id} className="card" onClick={() => navigate(`/doctors/${hospital.id}`)}>
              <img src={hospital.image} alt={hospital.name} className="card-img" />
              <h3 className="card-title">{hospital.name}</h3>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default HospitalSelection;
