import React from "react";
import "./LandingPage.css";

const LandingPage = () => {
  return (
    <div className="landing-container">
      <div className="form-container">
        <div className="tabs">
          <button className="active">Login</button>
          <button>Sign Up</button>
        </div>
        <input type="text" placeholder="Username" className="input-field" />
        <input type="password" placeholder="Password" className="input-field" />
        <button className="login-btn">Login</button>
      </div>
      <div className="welcome-section">
        <h1>Welcome to KidsArt Canvas!</h1>
        <p>A safe and fun place where young artists aged 5-15 can create, share, and explore amazing artwork!</p>
        <div className="images">
          <img src="https://writingsonthewall.in/cdn/shop/files/Sunny-Blues-Acrylic-on-Canvas-Painting-Writings-On-The-Wall-Oil-Painting_40432942711063.jpg?v=1699476640&width=1100" alt="Art Supplies" />
          <img src="https://ihbp.org.in/wp-content/uploads/2024/11/SURAJ-BARUI-HI-18X14.jpg" alt="Canvas Painting" />
        </div>
      </div>
    </div>
  );
};
