import "./HospitalDoctorSelection.css";
import React from "react";
import { useNavigate } from "react-router-dom";



const hospitals = [
  { id: "1", name: "City Hospital" },
  { id: "2", name: "Green Valley Clinic" },
  { id: "3", name: "Sunrise Medical Center" },
];

const HospitalSelection = () => {
  const navigate = useNavigate();

  const handleSelectHospital = (hospitalId) => {
    navigate(`/doctors/${hospitalId}`);
  };

  return (
    <div className="selection-container">
      <h2>Select a Hospital</h2>
      <ul>
        {hospitals.map((hospital) => (
          <li key={hospital.id}>
            <button onClick={() => handleSelectHospital(hospital.id)}>
              {hospital.name}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default HospitalSelection;
